CREATE OR REPLACE FUNCTION obter_se_depart_lib_setor (cd_departamento_p integer) RETURNS varchar AS $body$
DECLARE


ie_possui_departamento_w	varchar(1) := 'N';


BEGIN

if (coalesce(cd_departamento_p,0) > 0) then
	select	coalesce(max('S'),'N') ie_possui_departamento
	into STRICT	ie_possui_departamento_w
	from 	departamento_setor a
	where 	a.cd_departamento = cd_departamento_p
	and		exists (	SELECT	1
						from	usuario_setor x
						where	nm_usuario_param = wheb_usuario_pck.get_nm_usuario
						and		a.cd_setor_atendimento 	= x.cd_setor_atendimento);
end if;

return	ie_possui_departamento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
