CREATE OR REPLACE FUNCTION obter_se_depto_setor ( cd_departamento_p bigint, cd_setor_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


cont_w    bigint;


BEGIN

if (coalesce(cd_setor_atendimento_p::text, '') = '') then
    return 'N';
else
    select	count(*)
    into STRICT	cont_w
    from	departamento_setor
    where	cd_setor_atendimento	= cd_setor_atendimento_p
    and		cd_departamento		= cd_departamento_p;


    if (cont_w > 0) then
        return 'S';
    else
        return 'N';
    end if;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
