CREATE OR REPLACE FUNCTION obter_se_desconto_liberado (nm_usuario_p text, pr_desconto_p bigint) RETURNS varchar AS $body$
DECLARE


ie_liberado_w		varchar(1);
nr_sequencia_w		bigint;
nm_usuario_lib_w	varchar(15);
cd_setor_atendimento_w  bigint;
cd_setor_usuario_w	bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	regra_lib_desconto
	where	pr_desconto_p between pr_min and pr_max
	and	ie_situacao = 'A';

C02 CURSOR FOR
	SELECT 	nm_usuario_lib,
		cd_setor_atendimento
	from	regra_lib_desc_item
	where	nr_seq_regra = nr_sequencia_w;


BEGIN
ie_liberado_w := 'N';
cd_setor_usuario_w := obter_setor_usuario(nm_usuario_p);

open C01;
loop
fetch C01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	open C02;
	loop
	fetch C02 into
		nm_usuario_lib_w,
		cd_setor_atendimento_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin
		if (nm_usuario_lib_w = nm_usuario_p) then
			ie_liberado_w := 'S';
		end if;
		if (cd_setor_atendimento_w = cd_setor_usuario_w) then
			ie_liberado_w := 'S';
		end if;
		end;
	end loop;
	close C02;

	end;
end loop;
close C01;

return	ie_liberado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
