CREATE OR REPLACE FUNCTION obter_se_desc_ato_cirurgico (nr_cirurgia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w   varchar(255);


BEGIN

select CASE WHEN count(*)=0 THEN wheb_mensagem_pck.get_texto(854006)  ELSE wheb_mensagem_pck.get_texto(854007) END
into STRICT   ds_retorno_w
from   cirurgia_descricao a
where  ie_situacao = 'A'
and    (a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
and    nr_cirurgia = nr_cirurgia_p;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
