CREATE OR REPLACE FUNCTION obter_se_desfecho_atend ( nr_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE

qt_desfecho_w bigint;


BEGIN

	select coalesce(count(b.nr_sequencia),0)
	into STRICT 	qt_desfecho_w
	from 	atendimento_alta b, parametro_medico p
	where	nr_atendimento = nr_atendimento_p
	and 	ie_tipo_orientacao = 'P'
	and     p.cd_estabelecimento = obter_estabelecimento_ativo
	and     ((coalesce(p.ie_liberar_desfecho,'N')  = 'N')
	or      ((b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '') and (coalesce(b.dt_inativacao::text, '') = '')));

RETURN qt_desfecho_w;

END;
