SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_desmarcar_sum ( cd_material_p bigint, cd_procedimento_p text, nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
cd_grupo_material_w		smallint;
cd_subgrupo_material_w	smallint;
cd_classe_material_w	integer;

cd_area_procedimento_w	bigint;
cd_grupo_proc_w		bigint;
cd_especialidade_w		bigint;

ie_retorno_w		varchar(1) := 'S';
ds_regra_proc_interno_w	varchar(4000) := '';

nr_seq_proc_interno_w	bigint;

cd_procedimento_regra_w	bigint;
ie_origem_proced_regra_w	bigint;

ds_proc_w		varchar(50);

C01 CURSOR FOR 
	SELECT	nr_proc_interno 
	from	sumario_alta_regra 
	where	(nr_proc_interno IS NOT NULL AND nr_proc_interno::text <> '') 
	and	coalesce(ie_desmarcar,'N') = 'N';

C02 CURSOR FOR 
	SELECT	nr_sequencia 
	from	proc_interno 
	where	cd_procedimento || '_' || ie_origem_proced = ds_proc_w;


BEGIN 
 
if (coalesce(cd_material_p,0) > 0) then 
 
	select	max(cd_grupo_material), 
		max(cd_subgrupo_material), 
		max(cd_classe_material) 
	into STRICT	cd_grupo_material_w, 
		cd_subgrupo_material_w, 
		cd_classe_material_w 
	from	estrutura_material_v 
	where	cd_material = cd_material_p;
 
	select	coalesce(max('S'),'N') 
	into STRICT	ie_retorno_w 
	from	sumario_alta_regra 
	where	coalesce(cd_material,cd_material_p) = cd_material_p 
	and	coalesce(cd_grupo_material,cd_grupo_material_w) = cd_grupo_material_w 
	and	coalesce(cd_subgrupo_material,cd_subgrupo_material_w) = cd_subgrupo_material_w 
	and	coalesce(cd_classe_material,cd_classe_material_w) = cd_classe_material_w 
	and	coalesce(cd_material,coalesce(cd_grupo_material,coalesce(cd_subgrupo_material,coalesce(cd_classe_material,0)))) > 0 
	and	coalesce(ie_desmarcar,'N') = 'N';
 
elsif (cd_procedimento_p IS NOT NULL AND cd_procedimento_p::text <> '') then 
 
	ds_proc_w 	:= substr(cd_procedimento_p,1,instr(cd_procedimento_p,'_',1,2)-1);
 
	select	cd_area_procedimento, 
		cd_grupo_proc, 
		cd_especialidade 
	into STRICT	cd_area_procedimento_w, 
		cd_grupo_proc_w, 
		cd_especialidade_w 
	from	estrutura_procedimento_v 
	where	cd_procedimento || '_' || ie_origem_proced = ds_proc_w;
 
	select	coalesce(max('S'),'N') 
	into STRICT	ie_retorno_w 
	from	sumario_alta_regra 
	where	cd_procedimento || '_' || ie_origem_proced = ds_proc_w 
	and	coalesce(cd_area_procedimento,cd_area_procedimento_w) = cd_area_procedimento_w 
	and	coalesce(cd_grupo_proc,cd_grupo_proc_w) = cd_grupo_proc_w 
	and	coalesce(cd_especialidade,cd_especialidade_w) = cd_especialidade_w 
	and	coalesce(cd_procedimento,coalesce(ie_origem_proced,coalesce(cd_area_procedimento,coalesce(cd_grupo_proc,coalesce(cd_especialidade,0))))) > 0 
	and	coalesce(ie_desmarcar,'N') = 'N';
 
	if (ie_retorno_w = 'N') then 
		open C01;
		loop 
		fetch C01 into	 
			nr_seq_proc_interno_w;
		exit when(C01%notfound) or (ie_retorno_w = 'S');
			begin 
			ds_regra_proc_interno_w := ds_regra_proc_interno_w || ',' || nr_seq_proc_interno_w;
			 
			SELECT * FROM Obter_Proc_Tab_Interno(nr_seq_proc_interno_w, null, nr_atendimento_p, 0, cd_procedimento_regra_w, ie_origem_proced_regra_w) INTO STRICT cd_procedimento_regra_w, ie_origem_proced_regra_w;
			 
			if (ds_proc_w = (cd_procedimento_regra_w||'_'||ie_origem_proced_regra_w)) then 
				ie_retorno_w := 'S';
			end if;
			end;
		end loop;
		close C01;
 
		open C02;
		loop 
		fetch C02 into	 
			nr_seq_proc_interno_w;
		exit when(C02%notfound) or (ie_retorno_w = 'S');
			begin 
			if (obter_se_contido_char(nr_seq_proc_interno_w,ds_regra_proc_interno_w) = 'S') then 
				select	coalesce(max('S'),'N') 
				into STRICT	ie_retorno_w 
				from	sumario_alta_regra 
				where	nr_proc_interno = nr_seq_proc_interno_w 
				and	coalesce(ie_desmarcar,'N') = 'N';
			end if;
			end;
		end loop;
		close C02;
	end if;
 
end if;
 
return	ie_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
