CREATE OR REPLACE FUNCTION obter_se_destino_informado (nr_seq_viagem_p bigint) RETURNS varchar AS $body$
DECLARE


qt_destino_w	integer:= 0;
ie_retorno_w	varchar(1):= 'N';


BEGIN

begin
select	count(*)
into STRICT	qt_destino_w
from 	via_destino
where 	nr_seq_viagem = nr_seq_viagem_p;
exception
	when others then
	qt_destino_w:= 0;
end;

if (qt_destino_w > 0) then
	ie_retorno_w:= 'S';
else
	ie_retorno_w:= 'N';
end if;

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
