CREATE OR REPLACE FUNCTION obter_se_destino_liberado (ie_destino_remessa_p text, cd_perfil_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w  		varchar(3) := 'N';
cd_setor_atendimento_w	bigint := 0;
cd_perfil_w		integer := 0;
qt_total_w			bigint;

c01 CURSOR FOR
	SELECT 	coalesce(cd_setor_atendimento,0),
		coalesce(cd_perfil,0)
	from 	cf_liberacao_destino
	where	ie_destino_remessa = ie_destino_remessa_p;


BEGIN

select  count(*)
into STRICT	qt_total_w
from 	cf_liberacao_destino;

if (qt_total_w = 0) then
    ds_retorno_w := 'S';
else
	begin
	ds_retorno_w := 'N';
	open C01;
	loop
	fetch C01 into
		cd_setor_atendimento_w,
		cd_perfil_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		ds_retorno_w:= 'S';
		if  ((cd_setor_atendimento_w > 0)  and (obter_se_usuario_setor(nm_usuario_p, cd_setor_atendimento_w) = 'N')) then
		    ds_retorno_w := 'N';
		end if;
		if  (cd_perfil_w > 0 AND cd_perfil_w <> cd_perfil_p) then
		    ds_retorno_w := 'N';
		end if;
		end;
	end loop;
	close C01;
	end;
end if;
return	ds_retorno_w;

end;
$body$
