CREATE OR REPLACE FUNCTION obter_se_devedor_informado (nr_titulo_p bigint) RETURNS varchar AS $body$
DECLARE

 
cd_convenio_w		bigint;
cd_tipo_portador_w	bigint;
cd_portador_w		bigint;
nr_seq_terc_conta_w	bigint;
nr_seq_contrato_w		bigint;
ie_origem_titulo_w		varchar(100);
ie_tipo_devedor_w		varchar(100);
ds_convenio_w		varchar(255);


BEGIN 
 
select	coalesce(cd_convenio, 0), 
	cd_tipo_portador, 
	cd_portador, 
	ie_origem_titulo, 
	nr_seq_terc_conta, 
	nr_seq_contrato 
into STRICT	cd_convenio_w, 
	cd_tipo_portador_w, 
	cd_portador_w, 
	ie_origem_titulo_w, 
	nr_seq_terc_conta_w, 
	nr_seq_contrato_w 
from 	titulo_receber_v 
where	nr_titulo	= nr_titulo_p;
 
if (cd_convenio_w <> 0) then 
	ds_convenio_w	:= substr(obter_nome_convenio(cd_convenio_w),1,40);
elsif (nr_seq_terc_conta_w IS NOT NULL AND nr_seq_terc_conta_w::text <> '') then 
	ds_convenio_w := wheb_mensagem_pck.get_texto(309457); -- Contas de terceiros 
elsif (nr_seq_contrato_w IS NOT NULL AND nr_seq_contrato_w::text <> '') then
	ds_convenio_w := wheb_mensagem_pck.get_texto(309458); -- Contratos 
else
	ie_tipo_devedor_w := obter_regra_eis_titulo_venc(cd_tipo_portador_w, cd_portador_w, ie_origem_titulo_w, ie_tipo_devedor_w);
	ds_convenio_w := coalesce(substr(obter_valor_dominio(2919,ie_tipo_devedor_w),1,255),null);
end if;
 
if (coalesce(ds_convenio_w::text, '') = '') then 
	return	'N';
else 
	return	'S';
end if;	
 
end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
