SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_diag_classif_atend ( ds_atributo_p text, nr_seq_interno_p bigint, nr_atendimento_P bigint, ie_classif_diag_p text) RETURNS varchar AS $body$
DECLARE

				
ds_retorno_w	varchar(1) := 'S';
ie_opcao_wl_w	varchar(1);
ds_comando_w	varchar(4000);
			

BEGIN

select	max(ie_opcao_wl)
into STRICT	ie_opcao_wl_w
from	wl_worklist a,
		WL_REGRA_ITEM b
where	nr_seq_diagnostico = nr_seq_interno_p
and		a.nr_seq_regra = b.nr_sequencia
and		a.ie_classif_diag = ie_classif_diag_p;

ds_comando_w	:= 	'select		nvl(max(''N''),''S'') ie_tem_diag
					 from		diagnostico_doenca
					 where		nr_seq_interno <> '||nr_seq_interno_p||
					' and		'||ds_atributo_p||' = ''S''';
					
if (ie_opcao_wl_w = 'A')then
	ds_comando_w	:= ds_comando_w || ' and		nr_atendimento = '||nr_atendimento_p;
else
	ds_comando_w	:= ds_comando_w || ' and nr_atendimento in (select 	a.nr_atendimento 
																from   	atendimento_paciente a, 
																		episodio_paciente b 
																where 	a.nr_seq_episodio = b.nr_sequencia 
																and 	b.nr_sequencia = obter_episodio_atendimento('||nr_atendimento_p||'))';
end if;

EXECUTE ds_comando_w
into STRICT   ds_retorno_w;		

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
