CREATE OR REPLACE FUNCTION obter_se_diag_confirmado_html ( nr_prescricao_p bigint, nr_seq_diag_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(40) := substr(obter_desc_expressao(331675),1,40);
qt_existe_w	bigint;

/* S Sugerido - C Confirmado */

BEGIN

SELECT	COUNT(*)
INTO STRICT	qt_existe_w
FROM	pe_prescr_diag
WHERE	nr_seq_prescr	= nr_prescricao_p
AND	nr_seq_diag	= nr_seq_diag_p;

IF (qt_existe_w > 0) THEN
	ds_retorno_w	:= substr(obter_desc_expressao(285693),1,40);
END IF;

RETURN ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
