CREATE OR REPLACE FUNCTION obter_se_diag_confirmado ( nr_prescricao_p bigint, nr_seq_diag_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1) := 'S';
qt_existe_w	bigint;

/* S Sugerido - C Confirmado */

BEGIN

select	count(*)
into STRICT	qt_existe_w
from	pe_prescr_diag
where	nr_seq_prescr	= nr_prescricao_p
and	nr_seq_diag	= nr_seq_diag_p;

if (qt_existe_w > 0) then
	ds_retorno_w	:= 'C';
end if;

Return ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
