CREATE OR REPLACE FUNCTION obter_se_dialise_vig ( nr_prescricao_p bigint, nr_seq_solucao_p bigint, nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_protocolo_w	bigint;
dt_inicio_prescr_w	timestamp;
dt_validade_prescr_w	timestamp;
ie_existe_w		varchar(1) := 'S';


BEGIN

select	max(a.nr_seq_protocolo),
	max(b.dt_inicio_prescr),
	max(b.dt_validade_prescr)
into STRICT	nr_seq_protocolo_w,
	dt_inicio_prescr_w,
	dt_validade_prescr_w
from	prescr_solucao a,
	prescr_medica b
where	a.nr_prescricao		= b.nr_prescricao
and	a.nr_prescricao		= nr_prescricao_p
and	a.nr_seq_solucao	= nr_seq_solucao_p;

select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ie_existe_w
from	prescr_medica a,
	prescr_solucao b
where	a.nr_atendimento	= nr_atendimento_p
and	a.nr_prescricao		= b.nr_prescricao
and	a.dt_validade_prescr	between clock_timestamp() - interval '10 days' and clock_timestamp() + interval '5 days'
and	obter_se_prescr_vig_adep(a.dt_inicio_prescr,a.dt_validade_prescr,dt_inicio_prescr_w,dt_validade_prescr_w) = 'S'
and	((a.nr_prescricao	<> nr_prescricao_p) or (b.nr_seq_solucao <> nr_seq_solucao_p))
and	b.nr_seq_protocolo	= nr_seq_protocolo_w
and	coalesce(b.dt_suspensao::text, '') = '';

return	ie_existe_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
