CREATE OR REPLACE FUNCTION obter_se_dia_bloqueado_agenda ( cd_agenda_p bigint, dt_agenda_p timestamp) RETURNS varchar AS $body$
DECLARE


ie_dia_semana_w	smallint;
ie_bloqueado_w	varchar(1) := 'N';


BEGIN
if (cd_agenda_p IS NOT NULL AND cd_agenda_p::text <> '') and (dt_agenda_p IS NOT NULL AND dt_agenda_p::text <> '') then
	begin
	ie_dia_semana_w	:= (obter_cod_dia_semana(dt_agenda_p))::numeric;

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_bloqueado_w
	from	agenda_bloqueio
	where	cd_agenda = cd_agenda_p
	and	dt_agenda_p between dt_inicial and dt_final
	and	((ie_dia_semana = ie_dia_semana_w) or ((ie_dia_semana = 9) and (ie_dia_Semana_w not in (7,1))) or (coalesce(ie_dia_semana::text, '') = ''));
	end;
end if;
return ie_bloqueado_w;
end;
$body$
LANGUAGE PLPGSQL
