SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_dia_ciclo_autorizado ( nr_seq_paciente_setor_p bigint, nr_ciclo_p bigint) RETURNS varchar AS $body$
DECLARE

NR_SEQ_ESTAGIO_w	bigint;
ds_retorno_w		varchar(10)	:= 'N';
ie_interno_w		varchar(10);
nr_seq_paciente_w	bigint;
ds_dia_ciclo_w      	paciente_atendimento.ds_dia_ciclo%type;

BEGIN

select	max(nr_seq_paciente), max(ds_dia_ciclo)
into STRICT	nr_seq_paciente_w, ds_dia_ciclo_w
from	paciente_atendimento
where	nr_seq_atendimento = nr_seq_paciente_setor_p;


select	max(NR_SEQ_ESTAGIO)
into STRICT	NR_SEQ_ESTAGIO_w
from	autorizacao_convenio a
where	nr_seq_paciente  = nr_seq_paciente_setor_p
and     nr_sequencia = (SELECT max(b.nr_sequencia) from autorizacao_convenio b where	b.nr_seq_paciente  = a.nr_seq_paciente);

if (coalesce(NR_SEQ_ESTAGIO_w,0) = 0) and (coalesce(nr_seq_paciente_w,0) > 0)then

	select	max(a.NR_SEQ_ESTAGIO)
	into STRICT	NR_SEQ_ESTAGIO_w
	from	autorizacao_convenio a, estagio_autorizacao b
	where	a.nr_seq_paciente_setor = nr_seq_paciente_w
	and	a.NR_CICLO		= nr_ciclo_p
	and     a.ds_dia_ciclo 		= ds_dia_ciclo_w
	and	a.nr_seq_estagio	= b.nr_sequencia
	and	b.ie_interno 		not in ('90','70');

end if;

if (coalesce(NR_SEQ_ESTAGIO_w,0) = 0) and (coalesce(nr_seq_paciente_w,0) > 0)then

	select	max(a.NR_SEQ_ESTAGIO)
	into STRICT	NR_SEQ_ESTAGIO_w
	from	autorizacao_convenio a, estagio_autorizacao b
	where	a.nr_seq_paciente_setor = nr_seq_paciente_w
	and	a.NR_CICLO		= nr_ciclo_p
	and	a.nr_seq_estagio	= b.nr_sequencia
	and	b.ie_interno 		not in ('90','70');

end if;


if (NR_SEQ_ESTAGIO_w IS NOT NULL AND NR_SEQ_ESTAGIO_w::text <> '') then
	select	max(IE_INTERNO)
	into STRICT	ie_interno_w
	from	ESTAGIO_AUTORIZACAO
	where	nr_sequencia	= NR_SEQ_ESTAGIO_w
	and	OBTER_EMPRESA_ESTAB(wheb_usuario_pck.get_cd_estabelecimento) = cd_empresa;
	
	if (ie_interno_w	= '10') then
		ds_retorno_w	:= 'S';
	end if;
end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
