CREATE OR REPLACE FUNCTION obter_se_dia_relat_viagem ( nr_seq_viagem_p bigint, qt_dias_limite_p bigint, dt_despesa_p timestamp) RETURNS varchar AS $body$
DECLARE


dt_saida_prev_w			timestamp;
dt_retorno_prev_w		timestamp;
ie_perido_liberado_w		varchar(10);

BEGIN
select	trunc(dt_saida_prev) - qt_dias_limite_p,
	trunc(dt_retorno_prev) + qt_dias_limite_p
into STRICT	dt_saida_prev_w,
	dt_retorno_prev_w
from	via_viagem
where	nr_sequencia	= nr_seq_viagem_p;

select	coalesce(max('S'),'N')
into STRICT	ie_perido_liberado_w

where	trunc(dt_despesa_p) between trunc(dt_saida_prev_w) and fim_dia(dt_retorno_prev_w);

return	ie_perido_liberado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
