CREATE OR REPLACE FUNCTION obter_se_dia_trabalho ( cd_dia_semana_p bigint) RETURNS varchar AS $body$
DECLARE


is_dia_trabalho_w	varchar(1) := 'S';
ds_dia_semana_w		varchar(15);


BEGIN
if (cd_dia_semana_p < 8) then

	select	to_char(clock_timestamp() - pkg_date_utils.get_WeekDay(clock_timestamp())+ cd_dia_semana_p,'DY') -- Pega a descrição abreviada do dia da semana (Ex: SEG, TER, quando em PT-BR, e LUN, MAR em ESP-MX e MON, TUE em ING-EU)
	into STRICT	ds_dia_semana_w
	;

	select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
	into STRICT	is_dia_trabalho_w
	
	where	ds_dia_semana_w = obter_desc_expressao(687962) --SÁB
	or	ds_dia_semana_w = obter_desc_expressao(687964); --DOM
end if;

return	is_dia_trabalho_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
