CREATE OR REPLACE FUNCTION obter_se_dia_valido_radio (nr_seq_equip_p bigint, dt_agenda_p timestamp, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1);


BEGIN

select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ie_retorno_w
from	rxt_equip_regra_agenda
where	nr_seq_equipamento	= nr_seq_equip_p
and	((dt_dia_semana		= rxt_obter_cod_dia_semana(dt_agenda_p)) or ((dt_dia_semana = 9) and (rxt_obter_cod_dia_semana(dt_agenda_p) not in (7,1))))
and	(((ie_feriado = 'N')  and (obter_se_feriado(cd_estabelecimento_p, dt_agenda_p) = 0)) or (ie_feriado = 'S'))
and	to_char(hr_inicial,'hh24:mi:ss')	< to_char(hr_final,'hh24:mi:ss')
and	((coalesce(dt_inicio_vigencia::text, '') = '') 	or (dt_inicio_vigencia	<= trunc(dt_agenda_p)))
and	((coalesce(dt_final_vigencia::text, '') = '') 	or (dt_final_vigencia	>= trunc(dt_agenda_p)))
and	coalesce(nr_minuto_duracao,0)	> 0;


return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
