CREATE OR REPLACE FUNCTION obter_se_dieta_acomp ( nr_atendimento_p bigint, dt_servico_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1);
			

BEGIN

select 	CASE WHEN coalesce(max(a.qt_dieta_acomp),0)=0 THEN 'N'  ELSE 'S' END
into STRICT	ds_retorno_w
from	atend_categoria_convenio a
where	a.nr_atendimento = nr_atendimento_p
and	a.nr_seq_interno = (	SELECT	max(b.nr_seq_interno)
				from	atend_categoria_convenio b
				where	b.nr_atendimento = a.nr_atendimento
				and	ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(b.dt_inicio_vigencia) <= ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(dt_servico_p));

return	ds_retorno_w;

end;
