CREATE OR REPLACE FUNCTION obter_se_dieta_cpoe_susp (nr_seq_dieta_cpoe_p bigint, nr_prescricao_p bigint, ie_suspenso_p text) RETURNS varchar AS $body$
DECLARE


retorno_w 			varchar(1);
cd_funcao_origem_w	prescr_medica.cd_funcao_origem%type;


BEGIN

retorno_w := ie_suspenso_p;

select 	max(cd_funcao_origem)
into STRICT	cd_funcao_origem_w
from 	prescr_medica
where 	nr_prescricao = nr_prescricao_p;

if (coalesce(nr_seq_dieta_cpoe_p,0) > 0) and (cd_funcao_origem_w = 2314) and (ie_suspenso_p = 'N') then

	select 	coalesce(max('N'),'S')
	into STRICT 	retorno_w
	from 	prescr_dieta_hor a,
			prescr_dieta b
	where 	a.nr_prescricao = b.nr_prescricao
	and 	a.nr_seq_dieta = b.nr_sequencia
	and 	a.nr_prescricao = nr_prescricao_p
	and 	b.nr_seq_dieta_cpoe = nr_seq_dieta_cpoe_p
	and 	a.dt_horario > clock_timestamp()
	and 	coalesce(a.dt_suspensao::text, '') = '';
	
end if;

return retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
