CREATE OR REPLACE FUNCTION obter_se_dieta_hor_excl_ref (cd_dieta_p bigint, cd_refeicao_p text) RETURNS varchar AS $body$
DECLARE


ie_hor_excl_ref_w	varchar(1) := 'N';


BEGIN
if (cd_refeicao_p IS NOT NULL AND cd_refeicao_p::text <> '') and (cd_dieta_p IS NOT NULL AND cd_dieta_p::text <> '') then

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_hor_excl_ref_w
	from	horario_refeicao_dieta
	where	cd_dieta	= cd_dieta_p
	and	cd_refeicao = cd_refeicao_p;

end if;

return ie_hor_excl_ref_w;

end;
