CREATE OR REPLACE FUNCTION obter_se_dieta_rec_alerta ( cd_dieta_p bigint, nr_seq_regra_p bigint) RETURNS varchar AS $body$
DECLARE


ie_recebe_w	varchar(1);
qt_existe_regra_w	bigint;


BEGIN

ie_recebe_w := 'S';

select	count(nr_seq_regra)
into STRICT	qt_existe_regra_w
from	regra_envio_sms_dieta
where	nr_seq_regra = nr_seq_regra_p;

if (qt_existe_regra_w = 0) then
	ie_recebe_w := 	'S';
else
	select	coalesce(max('S'),'N')
	into STRICT	ie_recebe_w
	from	regra_envio_sms_dieta
	where	cd_dieta	= cd_dieta_p
	and	nr_seq_regra 	= nr_seq_regra_p;
end if;

return	ie_recebe_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
