CREATE OR REPLACE FUNCTION obter_se_dieta_sem_classif_sel (nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


ie_dieta_w	varchar(1) := 'N';


BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then
	begin
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_dieta_w
	from	dieta a
	where	a.ie_situacao = 'A'
	and	coalesce(a.nr_seq_classif::text, '') = ''
	and	a.cd_dieta not in (	SELECT	b.cd_dieta
					from	prescr_dieta b
					where	b.nr_prescricao = nr_prescricao_p
					and	b.cd_dieta 	= a.cd_dieta);
	end;
end if;
return ie_dieta_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
