CREATE OR REPLACE FUNCTION obter_se_dieta_valida ( cd_dieta_p bigint, cd_estabelecimento_p bigint, dt_prescricao_p timestamp) RETURNS varchar AS $body$
DECLARE


ie_dia_semana_w		smallint;
ie_permite_prescr_w	varchar(1) := 'S';
ie_segunda_w		varchar(1);
ie_terca_w		varchar(1);
ie_quarta_w		varchar(1);
ie_quinta_w		varchar(1);
ie_sexta_w		varchar(1);
ie_sabado_w		varchar(1);
ie_domingo_w		varchar(1);
ie_feriado_w		varchar(1);
qt_feriado_w		integer;
cont_w			integer;


BEGIN

select	count(*)
into STRICT	cont_w
from	rep_regra_dieta_dia
where	cd_dieta = cd_dieta_p;

if (cont_w > 0) then

	select	PKG_DATE_UTILS.get_WeekDay(dt_prescricao_p)
	into STRICT	ie_dia_semana_w
	;

	select	max(ie_segunda),
		max(ie_terca),
		max(ie_quarta),
		max(ie_quinta),
		max(ie_sexta),
		max(ie_sabado),
		max(ie_domingo),
		max(ie_feriado)
	into STRICT	ie_segunda_w,
		ie_terca_w,
		ie_quarta_w,
		ie_quinta_w,
		ie_sexta_w,
		ie_sabado_w,
		ie_domingo_w,
		ie_feriado_w
	from	rep_regra_dieta_dia
	where	cd_dieta = cd_dieta_p;

	if (ie_dia_semana_w	= 1) and (ie_domingo_w		= 'N') then
		ie_permite_prescr_w	:= 'N';
	elsif (ie_dia_semana_w	= 2) and (ie_segunda_w		= 'N') then
		ie_permite_prescr_w	:= 'N';
	elsif (ie_dia_semana_w	= 3) and (ie_terca_w		= 'N') then
		ie_permite_prescr_w	:= 'N';
	elsif (ie_dia_semana_w	= 4) and (ie_quarta_w		= 'N') then
		ie_permite_prescr_w	:= 'N';
	elsif (ie_dia_semana_w	= 5) and (ie_quinta_w		= 'N') then
		ie_permite_prescr_w	:= 'N';
	elsif (ie_dia_semana_w	= 6) and (ie_sexta_w		= 'N') then
		ie_permite_prescr_w	:= 'N';
	elsif (ie_dia_semana_w	= 7) and (ie_sabado_w		= 'N') then
		ie_permite_prescr_w	:= 'N';
	elsif (ie_feriado_w		= 'N') then

		select	max(Obter_Se_Feriado(cd_estabelecimento_p, dt_prescricao_p))
		into STRICT	qt_feriado_w
		;

		if (qt_feriado_w > 0) then
			ie_permite_prescr_w	:= 'N';
		end if;
	end if;
end if;

return ie_permite_prescr_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
