CREATE OR REPLACE FUNCTION obter_se_dieta_vigente_cpoe ( nr_atendimento_p bigint, cd_pessoa_fisica_p text ) RETURNS varchar AS $body$
DECLARE


    	ie_dieta_vigente_w   varchar(1);


BEGIN

    	select  	CASE WHEN count(*)=0 THEN  'N'  ELSE 'S' END
    	into STRICT    	ie_dieta_vigente_w
    	from    	cpoe_dieta
    	where   	((nr_atendimento   = nr_atendimento_p)
            		or (cd_pessoa_fisica = cd_pessoa_fisica_p
            	and 	coalesce(nr_atendimento::text, '') = ''))
    	and     	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
    	and     	coalesce(dt_suspensao::text, '') = ''
    	and (coalesce(dt_fim::text, '') = ''	or dt_fim > clock_timestamp());

    	return ie_dieta_vigente_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
