CREATE OR REPLACE FUNCTION obter_se_dieta_vigente ( nr_atendimento_p bigint, dt_referencia_p timestamp) RETURNS varchar AS $body$
DECLARE


ie_dieta_vigente_w		varchar(1) := 'N';


BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (dt_referencia_p IS NOT NULL AND dt_referencia_p::text <> '') then
	select	coalesce(max('S'),'N')
	into STRICT	ie_dieta_vigente_w
	from	prescr_medica a
	where	a.nr_atendimento = nr_atendimento_p
	and		dt_referencia_p between a.dt_inicio_prescr and a.dt_validade_prescr
	and		(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
	and		coalesce(a.dt_suspensao::text, '') = ''
	and		exists (SELECT	1
					from	prescr_dieta x
					where	a.nr_prescricao = x.nr_prescricao
					and		coalesce(x.dt_suspensao::text, '') = ''
					and		coalesce(x.ie_suspenso,'N')	<> 'S');
end if;

return	ie_dieta_vigente_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
