CREATE OR REPLACE FUNCTION obter_se_diluente ( nr_prescricao_p bigint, nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE


qt_retorno_w			bigint;
cd_material_w			integer;
cd_diluente_w			integer;
ie_via_aplicacao_w		varchar(5);
cd_estabelecimento_w		smallint;
qt_idade_w			double precision;
qt_peso_w			real;


BEGIN

/*Matheus OS 50978 20/03/07*/

select	a.cd_estabelecimento,
	obter_idade(b.dt_nascimento,coalesce(b.dt_obito,clock_timestamp()),'M'),
	coalesce(a.qt_peso,0)
into STRICT	cd_estabelecimento_w,
	qt_idade_w,
	qt_peso_w
from	pessoa_fisica b,
	prescr_medica a
where	a.nr_prescricao		= nr_prescricao_p
and	a.cd_pessoa_fisica	= b.cd_pessoa_fisica;

qt_idade_w	:= dividir(qt_idade_w,12);

select	cd_material,
	ie_via_aplicacao
into STRICT	cd_material_w,
	ie_via_aplicacao_w
from	prescr_material
where	nr_prescricao	= nr_prescricao_p
and	nr_sequencia	= nr_sequencia_p;

select	coalesce(max(cd_material),0)
into STRICT	cd_diluente_w
from	prescr_material
where	nr_prescricao		= nr_prescricao_p
and	nr_sequencia_diluicao	= nr_sequencia_p
and	ie_agrupador		= 3;

if (ie_via_aplicacao_w IS NOT NULL AND ie_via_aplicacao_w::text <> '') then
	select	count(*)
	into STRICT	qt_retorno_w
	from	material_diluicao
	where	cd_material		= cd_material_w
	and	cd_diluente		<> cd_diluente_w
	and	cd_estabelecimento	= cd_estabelecimento_w
	and	qt_idade_w between coalesce(qt_idade_min,0) and coalesce(qt_idade_max,999)
	and	qt_peso_w  between coalesce(qt_peso_min,0) and coalesce(qt_peso_max,999999)
	and	ie_reconstituicao	= 'N'
	and	((coalesce(ie_via_aplicacao::text, '') = '') or (ie_via_aplicacao = ie_via_aplicacao_w));
else
	select	count(*)
	into STRICT	qt_retorno_w
	from	material_diluicao
	where	cd_material		= cd_material_w
	and	cd_estabelecimento	= cd_estabelecimento_w
	and	qt_idade_w between coalesce(qt_idade_min,0) and coalesce(qt_idade_max,999)
	and	qt_peso_w  between coalesce(qt_peso_min,0) and coalesce(qt_peso_max,999999)
	and	cd_diluente		<> cd_diluente_w
	and	ie_reconstituicao	= 'N';
end if;

return	qt_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_se_diluente ( nr_prescricao_p bigint, nr_sequencia_p bigint) FROM PUBLIC;

