CREATE OR REPLACE FUNCTION obter_se_dispensa_ajuste (nr_seq_propaci_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1) := 'N';
ie_ignora_regra_w	varchar(1) := 'N';

C01 CURSOR FOR
	SELECT	ie_ignora_regra
	from	proc_pac_regra_ajuste
	where	nr_seq_propaci = nr_seq_propaci_p
	order by nr_sequencia;


BEGIN

open C01;
loop
fetch C01 into
	ie_ignora_regra_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ds_retorno_w:= ie_ignora_regra_w;
	end;
end loop;
close C01;

return	ds_retorno_w;

end;
$body$
