CREATE OR REPLACE FUNCTION obter_se_dispositivo_atrasado (nr_seq_dispositivo_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(1);


BEGIN

select 	max(coalesce('S','N'))
into STRICT	ds_retorno_w
from 	wl_worklist
where 	nr_seq_disp_pac	= nr_seq_dispositivo_p
and		dt_final_previsto < clock_timestamp()
