CREATE OR REPLACE FUNCTION obter_se_dispositivo_via ( nr_seq_dispositivo_p bigint, nr_seq_via_acesso_p bigint) RETURNS varchar AS $body$
DECLARE


qt_regras_w		bigint;
ie_exibe_w		varchar(1);


BEGIN

select	count(*)
into STRICT	qt_regras_w
from	via_acesso_dispositivo;

if (qt_regras_w > 0) then
	select	coalesce(max('S'),'N')
	into STRICT	ie_exibe_w
	from	via_acesso_dispositivo
	where	nr_seq_via_acesso	=	nr_seq_via_acesso_p
	and		nr_seq_dispositivo	=	nr_seq_dispositivo_p;
else
	ie_exibe_w	:= 'S';
end if;

return	ie_exibe_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
