SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_disp_ajustada ( nr_cirurgia_p bigint, cd_material_p bigint) RETURNS varchar AS $body$
DECLARE

nr_prescricao_w			bigint;
nr_prescricao_esp_w		bigint;
qt_consignado_w			bigint;
qt_material_w			bigint;
ie_consignado_w			varchar(1);
cd_material_estoque_w	integer;


BEGIN

select	coalesce(max(nr_prescricao),0)
into STRICT	nr_prescricao_w
from 	cirurgia
where 	nr_cirurgia = nr_cirurgia_p;

select	coalesce(max(ie_consignado),0)
into STRICT	ie_consignado_w
from	material
where	cd_material = cd_material_p;

if (ie_consignado_w = 1) then
	begin
	select	coalesce(max(nr_prescricao),0)
	into STRICT	nr_prescricao_esp_w
	from	prescr_medica
	where	nr_cirurgia	= nr_cirurgia_p
	and	nr_prescricao 	<> nr_prescricao_w
	and 	coalesce(ie_tipo_prescr_cirur,0) <> 2;

	select	coalesce(sum(qt_dispensacao),0)
	into STRICT	qt_consignado_w
	from    	material c,
		cirurgia_agente_disp a,
		cirurgia b
	where	a.nr_cirurgia   	= b.nr_cirurgia
	and     	a.cd_material   	= c.cd_material
	and	a.ie_operacao	= 'D'
	and	c.ie_consignado in (1)
	and	a.nr_cirurgia	= nr_cirurgia_p
	and	a.cd_material	= cd_material_p;

	select	coalesce(sum(x.qt_material),0)
	into STRICT	qt_material_w
	from    	material y,
		prescr_material x
	where	y.cd_material       	= x.cd_material
	and	x.nr_prescricao	= nr_prescricao_esp_w
	and	y.cd_material 	= cd_material_p;
	end;
else
	begin
	select	max(cd_material_estoque)
	into STRICT	cd_material_estoque_w
	from	material
	where	cd_material = cd_material_p;


	select	coalesce(sum(qt_dispensacao),0)
	into STRICT	qt_consignado_w
	from   	material c,
			cirurgia_agente_disp a,
			cirurgia b
	where	a.nr_cirurgia   	= b.nr_cirurgia
	and     a.cd_material   	= c.cd_material
	and		a.nr_cirurgia	= nr_cirurgia_p
	and		a.ie_operacao	= 'D'
	and (c.ie_consignado not in (1))
	and		((a.cd_material	= cd_material_p) or (c.cd_material_estoque = cd_material_estoque_w));

	select	coalesce(sum(x.qt_material),0)
	into STRICT	qt_material_w
	from    material y,
			prescr_material x
	where	y.cd_material	= x.cd_material
	and		x.nr_prescricao	= nr_prescricao_w
	and		((y.cd_material	= cd_material_p) or (y.cd_material_estoque = cd_material_estoque_w));
	end;
end if;

if	((qt_consignado_w - qt_material_w) > 0) then
	return 	'N';
else
	return	'S';
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_se_disp_ajustada ( nr_cirurgia_p bigint, cd_material_p bigint) FROM PUBLIC;

