CREATE OR REPLACE FUNCTION obter_se_diverg_exige_justif ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ie_exige_w			varchar(1);


BEGIN

select	coalesce(max(ie_exige_justificativa),'N')
into STRICT	ie_exige_w
from	inspecao_tipo_divergencia
where	nr_sequencia = nr_sequencia_p;

return	ie_exige_w;

end;
