CREATE OR REPLACE FUNCTION obter_se_diverg_justificada ( nr_seq_divergencia_p bigint) RETURNS varchar AS $body$
DECLARE


ie_justificada_w		varchar(1) := 'N';
qt_registros_w			bigint;


BEGIN

select	count(*)
into STRICT	qt_registros_w
from	diverg_inspecao_oc_justif
where	nr_seq_divergencia = nr_seq_divergencia_p;

if (qt_registros_w = 0) then
	ie_justificada_w := 'N';
else
	select	count(*)
	into STRICT	qt_registros_w
	from	diverg_inspecao_oc_justif
	where	nr_seq_divergencia = nr_seq_divergencia_p
	and	coalesce(nr_seq_tipo_justificativa::text, '') = ''
	and	coalesce(ds_justificativa::text, '') = '';

	if (qt_registros_w > 0) then
		ie_justificada_w := 'N';
	else
		ie_justificada_w := 'S';
	end if;
end if;

return	ie_justificada_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
