CREATE OR REPLACE FUNCTION obter_se_diver_cont_just ( nr_documento_p bigint, ie_tipo_documento_p text) RETURNS varchar AS $body$
DECLARE


qt_existe_w		bigint;
ie_retorno_w		varchar(1);


BEGIN

if (ie_tipo_documento_p = 'SC') then
	begin

	select	count(*)
	into STRICT	qt_existe_w
	from	solic_compra_item a
	where	a.nr_solic_compra = nr_documento_p
	and	coalesce(ds_justif_diver::text, '') = ''
	and	a.nr_item_solic_compra in (SELECT	x.nr_item_documento
					from	diverg_contrato_item_doc x
					where	x.nr_documento = a.nr_solic_compra
					and	x.ie_tipo_documento = ie_tipo_documento_p);

	end;
elsif (ie_tipo_documento_p = 'OC') then
	begin

	select	count(*)
	into STRICT	qt_existe_w
	from	ordem_compra_item a
	where	a.nr_ordem_compra = nr_documento_p
	and	coalesce(ds_justif_diver::text, '') = ''
	and	a.nr_item_oci in (SELECT	x.nr_item_documento
				from	diverg_contrato_item_doc x
				where	x.nr_documento = a.nr_ordem_compra
				and	x.ie_tipo_documento = ie_tipo_documento_p);

	end;
end if;

if (qt_existe_w > 0) then
	ie_retorno_w	:= 'N';
else
	ie_retorno_w	:= 'S';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
