CREATE OR REPLACE FUNCTION obter_se_doacao_exame_pend (nr_Seq_doacao_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(1);


BEGIN

select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ds_retorno_w
from	SAN_EXAME_LOTE a,
	SAN_EXAME_REALIZADO b,
	san_exame c
where	nr_Seq_doacao = nr_Seq_doacao_p
and	b.NR_SEQ_EXAME_LOTE = a.nr_Sequencia
and	b.nr_seq_exame = c.nr_sequencia
and	c.ie_exige_senha = 'S'
and (coalesce(b.dt_liberacao::text, '') = '' or 
	upper(coalesce(DS_RESULTADO,'X')) <> upper('Negativo')
	and elimina_acentos(upper(coalesce(ds_resultado,'X'))) <> 'NAO REAGENTE'
     	and 	coalesce(c.ie_consiste_result_pos,'S') = 'S'
	and	coalesce(VL_RESULTADO,0) = 0
		and 	not exists(	SELECT 1 
					from SAN_EXAME_LOTE x, 
						san_exame_realizado y, 
						san_exame z
					where 	y.nr_seq_exame_lote = x.nr_sequencia
					and 	((coalesce(y.vl_resultado::text, '') = '' and
						upper(coalesce(y.ds_resultado,'X')) <> 'POSITIVO' and
						elimina_acentos(upper(coalesce(y.ds_resultado,'X'))) <> 'REAGENTE')
					and 	(y.ds_resultado IS NOT NULL AND y.ds_resultado::text <> '') 
					and 	coalesce(z.ie_consiste_result_pos,'S') = 'S')
					and	x.nr_amostra > a.nr_amostra
					and 	y.nr_seq_exame = c.nr_sequencia
					and 	x.nr_seq_doacao = a.nr_seq_doacao));

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
