CREATE OR REPLACE FUNCTION obter_se_doadora_multigesta ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1);


BEGIN

ie_retorno_w := 'N';

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	begin
	SELECT	'S'
	into STRICT	ie_retorno_w
	FROM	san_impedimento b,
		san_questionario c,
		san_doacao d
	WHERE	b.nr_sequencia 		= c.nr_seq_impedimento
	AND	c.nr_seq_doacao 	= d.nr_sequencia
	AND	b.ie_situacao 		= 'A'
	AND	c.nr_seq_doacao 	= nr_sequencia_p
	and	coalesce(ie_resposta,'N') 	= 'S'
	AND	coalesce(b.ie_multigesta,'N')= 'S'
	AND	((san_obter_sexo_pf_doador(d.cd_pessoa_fisica) = b.ie_sexo) OR (b.ie_sexo = 'A') OR (coalesce(b.ie_sexo::text, '') = ''));
	exception
		when others then
			ie_retorno_w := 'N';
	end;
end if;	
	
return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
