CREATE OR REPLACE FUNCTION obter_se_doc_convenio ( nr_seq_doc_atend_p bigint, ie_tipo_atendimento_p bigint, cd_perfil_p bigint, nr_seq_classificacao_p bigint, cd_funcao_aux_p bigint default 0, cd_empresa_p bigint default 0, ie_ignora_tipo_atend_p text default 'N') RETURNS varchar AS $body$
DECLARE

			
ds_retorno_w		varchar(1)	:= 'S';
qt_registro_w		bigint;
ie_tipo_atendimento_w	smallint;
cd_funcao_w		bigint;
cd_perfil_w		bigint;
nr_seq_classificacao_w	bigint;
cd_empresa_w		bigint;
dt_base_w		timestamp := clock_timestamp();


BEGIN

ie_tipo_atendimento_w	:= coalesce(ie_tipo_atendimento_p,0);
select 	CASE WHEN cd_perfil_p=0 THEN  coalesce(obter_perfil_ativo,0)  ELSE cd_perfil_p END
into STRICT	cd_perfil_w
;

nr_seq_classificacao_w	:= coalesce(nr_seq_classificacao_p,0);
cd_empresa_w		:= coalesce(cd_empresa_p,0);

select 	CASE WHEN cd_funcao_aux_p=0 THEN  coalesce(obter_funcao_ativa,0)  ELSE cd_funcao_aux_p END
into STRICT	cd_funcao_w
;

select	count(*)
into STRICT	qt_registro_w
from	convenio_doc_atend_regra
where	nr_seq_doc_atend	= nr_seq_doc_atend_p
and	ie_situacao		= 'A';

if (qt_registro_w > 0) then
	
	select	coalesce(max('S'), 'N')
	into STRICT	ds_retorno_w
	from	convenio_doc_atend_regra
	where	nr_seq_doc_atend = nr_seq_doc_atend_p
	and 	((coalesce(ie_tipo_atendimento, ie_tipo_atendimento_w) = coalesce(ie_tipo_atendimento_w,0)) or
		((coalesce(ie_tipo_atendimento_w,0) = 0) and (ie_ignora_tipo_atend_p = 'S')))
	and	((coalesce(nr_seq_classificacao,nr_seq_classificacao_w) = coalesce(nr_seq_classificacao_w,0)) or (coalesce(nr_seq_classificacao_w,0) = 0))
	and	((coalesce(cd_empresa_w,0) = 0) or (coalesce(cd_empresa,cd_empresa_w) = coalesce(cd_empresa_w,0)))
	and	((coalesce(cd_perfil,cd_perfil_w) = coalesce(cd_perfil_w,0)) or (coalesce(cd_perfil_w,0) = 0))
	and	((coalesce(cd_funcao,cd_funcao_w) = coalesce(cd_funcao_w,0)) or (coalesce(cd_funcao_w,0) = 0))
	and	dt_base_w between coalesce(dt_vigencia_inicial, dt_base_w) and coalesce(dt_vigencia_final, dt_base_w)
	and	dt_base_w between 
			to_date(to_char(coalesce(dt_base_w, clock_timestamp()),'dd/mm/yyyy') || ' ' ||
				coalesce(to_char(hr_inicial,'hh24:mi:ss'), '00:00:00'), 'dd/mm/yyyy hh24:mi:ss') and
			to_date(to_char(coalesce(dt_base_w, clock_timestamp()),'dd/mm/yyyy') || ' ' ||
				coalesce(to_char(hr_final,'hh24:mi:ss'), '23:59:59'), 'dd/mm/yyyy hh24:mi:ss')
	and	ie_situacao	= 'A';

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
