SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_doc_conv_lib ( nr_seq_doc_atend_p bigint, ie_tipo_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w		varchar(1)	:= 'S';
qt_registro_w		bigint;
ie_tipo_atendimento_w	smallint;
dt_base_w		timestamp:= clock_timestamp();


BEGIN

ie_tipo_atendimento_w	:= coalesce(ie_tipo_atendimento_p,0);

select	count(*)
into STRICT	qt_registro_w
from	convenio_doc_atend_regra
where	nr_seq_doc_atend	= nr_seq_doc_atend_p
and	ie_situacao		= 'A';

if (qt_registro_w		> 0) then

	select	count(*)
	into STRICT	qt_registro_w
	from	convenio_doc_atend_regra
	where	nr_seq_doc_atend				= nr_seq_doc_atend_p
	and	coalesce(ie_tipo_atendimento,ie_tipo_atendimento_w)	= ie_tipo_atendimento_w
	and	coalesce(cd_perfil,obter_perfil_ativo)		= obter_perfil_ativo
	and	dt_base_w	between coalesce(dt_vigencia_inicial, dt_base_w) and coalesce(dt_vigencia_final, dt_base_w)
	and	ie_situacao		= 'A';

	if (qt_registro_w	= 0) then
		ds_retorno_w	:= 'N';
	end if;
end if;

return	ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
