SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_doc_lido_usu ( nr_seq_doc_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


nr_seq_log_w		bigint;
dt_ultima_leitura_w		timestamp;
dt_revisao_w		timestamp;
ie_retorno_w		varchar(1);
ie_data_aprov_rev_w	varchar(1);/*Considera a data da aprovacao (S) ou data da revisao (N)*/


/*
N - Nao Lido
S - Lido
*/
BEGIN
ie_retorno_w	:= 'N';

ie_data_aprov_rev_w := obter_param_usuario(4000, 165, obter_perfil_Ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, ie_data_aprov_rev_w);

select	max(a.nr_sequencia)
into STRICT	nr_seq_log_w
from	qua_doc_log_acesso a
where	a.nr_seq_doc = nr_seq_doc_p
and	a.nm_usuario = nm_usuario_p
and	((dt_fim_leitura IS NOT NULL AND dt_fim_leitura::text <> '' AND dt_leitura IS NOT NULL AND dt_leitura::text <> '') or (dt_pendente IS NOT NULL AND dt_pendente::text <> ''));

select	max(a.dt_leitura)
into STRICT	dt_ultima_leitura_w
from	qua_doc_log_acesso a
where	a.nr_sequencia = nr_seq_log_w;

if (dt_ultima_leitura_w IS NOT NULL AND dt_ultima_leitura_w::text <> '') then
	begin
	select	max(dt_revisao)
	into STRICT	dt_revisao_w
	from	qua_doc_revisao
	where	nr_seq_doc = nr_seq_doc_p
	and	(dt_aprovacao IS NOT NULL AND dt_aprovacao::text <> '');

	if (coalesce(ie_data_aprov_rev_w,'N') = 'S') then
		select	max(dt_aprovacao)
		into STRICT	dt_revisao_w
		from	qua_doc_revisao
		where	nr_seq_doc = nr_seq_doc_p
		and	(dt_aprovacao IS NOT NULL AND dt_aprovacao::text <> '');
	end if;
	
	if (coalesce(dt_revisao_w::text, '') = '') or (dt_revisao_w <= dt_ultima_leitura_w) then
		ie_retorno_w	:= 'S';
	end if;
	end;
end if;	

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
