CREATE OR REPLACE FUNCTION obter_se_doc_nasc_obrig_ok (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(2) := 'N';


BEGIN

select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
into STRICT	ds_retorno_w
from 	parto a
WHERE  a.nr_atendimento = nr_atendimento_p
AND	   ((CASE WHEN a.QT_NASC_VIVOS=0 THEN QT_NASC_MORTOS  ELSE a.QT_NASC_VIVOS END  <>  (SELECT	COUNT(*)
	   					    FROM 	nascimento b
							WHERE	b.nr_atendimento = a.nr_atendimento)) OR
 (((SELECT COUNT(*)
     FROM  nascimento c
  WHERE c.nr_atendimento = a.nr_atendimento
  AND coalesce(c.nr_dnv::text, '') = '') > 0) and a.QT_NASC_VIVOS >0 ));

return	ds_retorno_w;

end;
