CREATE OR REPLACE FUNCTION obter_se_doc_qua_nao_lido ( cd_estabelecimento_p bigint, nm_usuario_p text, ie_lib_setor_usuario_p text, ie_consid_toda_regra_p text, -- Troca OR por AND
 nr_seq_idioma_usu_p bigint ) RETURNS varchar AS $body$
DECLARE


ie_doc_nao_lido_w	varchar(1) := 'N';


BEGIN
if (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT   	ie_doc_nao_lido_w
	from   	qua_documento a
	where  	cd_estabelecimento = cd_estabelecimento_p
	and    	coalesce(obter_se_doc_lido_usuario(a.nr_sequencia, nm_usuario_p),'N') = 'N'
	and    	obter_acesso_docto_qual(a.nr_sequencia, nm_usuario_p, cd_estabelecimento_p, ie_lib_setor_usuario_p,ie_consid_toda_regra_p) = 'S'
	and	(a.dt_aprovacao IS NOT NULL AND a.dt_aprovacao::text <> '')
	and    	a.ie_situacao = 'A'
	and	a.ie_status = 'D'
	and (a.nr_seq_idioma = nr_seq_idioma_usu_p or coalesce(a.nr_seq_idioma::text, '') = '');
	end;
end if;
return ie_doc_nao_lido_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
