CREATE OR REPLACE FUNCTION obter_se_dose_esp_ago ( nr_prescricao_p bigint, cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


qt_existe_w	bigint;
ie_dose_especial_agora_w varchar(1) := 'N';


BEGIN
select count(*)
into STRICT	qt_existe_w
from	prescr_material
where	nr_prescricao = nr_prescricao_p
and	cd_material = cd_material_p
and	ie_dose_espec_agora = 'S';

if (qt_existe_w = 0) then

	select count(*)
	into STRICT	qt_existe_w
	from	prescr_material
	where	nr_prescricao = nr_prescricao_p
	and	cd_material = cd_material_p
	and	ie_urgencia = 'S';
end if;

if (qt_existe_w > 0) then
	ie_dose_especial_agora_w := 'S';
end if;

return ie_dose_especial_agora_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
