CREATE OR REPLACE FUNCTION obter_se_dose_esp_zero (ds_dose_esp_p text) RETURNS varchar AS $body$
DECLARE

ie_posicao_w	bigint;
ds_resto_w		varchar(255);
qt_dose_w		varchar(15);
ds_retorno_w	varchar(1) := 'N';

BEGIN
ds_resto_w := ds_dose_esp_p;

loop
exit when(ie_posicao_w = 0) or (ds_retorno_w = 'S');
	begin
	ie_posicao_w := position('-' in ds_resto_w);
	if (ie_posicao_w > 0) then
		qt_dose_w	:= substr(ds_resto_w,1,ie_posicao_w-1);
	else
		qt_dose_w	:= ds_resto_w;
	end if;
	if (qt_dose_w = 0) then
		ds_retorno_w:= 'S';
		exit;
	end if;
	ds_resto_w 		:= substr(ds_resto_w,ie_posicao_w+1,255);
	end;
end loop;

return ds_retorno_w;

end;
$body$
