CREATE OR REPLACE FUNCTION obter_se_dose_limite_mat ( nr_seq_elem_mat_p bigint, cd_material_p bigint, qt_dose_p bigint) RETURNS varchar AS $body$
DECLARE


qt_max_w	double precision;
qt_min_w	double precision;
ds_retorno_w	varchar(255);

BEGIN
if (nr_seq_elem_mat_p IS NOT NULL AND nr_seq_elem_mat_p::text <> '') then
	select 	coalesce(max(qt_max),99999999999),
			coalesce(max(qt_min),0)
	into STRICT		qt_max_w,
			qt_min_w
	from		nut_elem_material
	where 	nr_sequencia = nr_seq_elem_mat_p;
	if	((coalesce(qt_dose_p,0) > qt_max_w) or (coalesce(qt_dose_p,0) < qt_min_w)) then
		ds_retorno_w := obter_desc_expressao(347742)||chr(13)|| --O valor informado está fora da faixa padrão deste produto.
						obter_desc_expressao(781805, 'QT_MIN_W' || qt_min_w || ';QT_MAX_W=' || qt_max_w);--'Faixa padrão: '||qt_min_w||' - '||qt_max_w||'.';
	end if;
elsif (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then
	select 	coalesce(max(qt_max),99999999999),
			coalesce(max(qt_min),0)
	into STRICT		qt_max_w,
			qt_min_w
	from		nut_elem_material
	where 	cd_material = cd_material_p;
	if	((coalesce(qt_dose_p,0) > qt_max_w) or (coalesce(qt_dose_p,0) < qt_min_w)) then
		ds_retorno_w := obter_desc_expressao(347742)||chr(13)|| --O valor informado está fora da faixa padrão deste produto.
						obter_desc_expressao(781805, 'QT_MIN_W' || qt_min_w || ';QT_MAX_W=' || qt_max_w);--'Faixa padrão: '||qt_min_w||' - '||qt_max_w||'.';
	end if;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
