CREATE OR REPLACE FUNCTION obter_se_dt_entrega_atrasada (nr_lote_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1) := 'N';


BEGIN

if (nr_lote_p IS NOT NULL AND nr_lote_p::text <> '') then
	select	coalesce(max('S'),'N')
	into STRICT	ie_retorno_w
	from 	lote_producao_comp
	where   nr_lote_producao = nr_lote_p
	and 	obter_data_entrega_prev_opme(cd_material,nr_seq_protocolo) < clock_timestamp();

end if;


return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
