SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_duplica_apac_proc (cd_procedimento_p bigint, ie_origem_proced_p bigint, nr_seq_interno_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w			varchar(1);
ie_duplica_w			varchar(1) := 'S';
ie_previo_w			varchar(1);
qt_mod_meses_apac_w		smallint;
qt_meses_autorizado_w		integer;
qt_usuario_excecao_w		bigint := 0;


BEGIN 
 
begin 
select	ie_proc_previo 
into STRICT	ie_previo_w 
from	sus_apac_proc_duplic 
where	cd_procedimento		= cd_procedimento_p 
and	ie_origem_proced	= ie_origem_proced_p;
exception 
	when others then 
		ie_duplica_w	:= 'S';
end;
 
if (ie_previo_w IS NOT NULL AND ie_previo_w::text <> '') then 
	 
	/*3 significa o total de meses de uma apac, esse teste verifica se o paciente foi a todos os meses autorizados OS66311*/
 
	select	mod(qt_meses_autorizado,3), 
		qt_meses_autorizado 
	into STRICT	qt_mod_meses_apac_w, 
		qt_meses_autorizado_w 
	from	sus_laudo_paciente 
	where	nr_seq_interno	= nr_seq_interno_p;
	 
	/*verifica se o procedimento é prévio e se qt meses autorizado é maior que 0 e se o paciente foi a todos os meses autorizados ou se o procedimento não é prévio*/
 
	if	((ie_previo_w 	= 'S') and (qt_mod_meses_apac_w = 0) and (qt_meses_autorizado_w > 0)) or (ie_previo_w	= 'N') then 
		ie_duplica_w	:= 'N';
	end if;
		 
	if (ie_duplica_w = 'N') then 
		begin 
 
		select	count(*) 
		into STRICT	qt_usuario_excecao_w 
		from	sus_apac_proc_dup_usuario a, 
			sus_apac_proc_duplic b 
		where	a.nr_seq_regra 		= b.nr_sequencia 
		and	a.nm_usuario_excecao 	= nm_usuario_p 
		and	b.cd_procedimento	= cd_procedimento_p 
		and	b.ie_origem_proced	= ie_origem_proced_p 
		and	a.ie_situacao = 'A';
	 
		if (qt_usuario_excecao_w > 0) then 
			begin 
			ie_duplica_w	:= 'S';
			end;
		end if;
		 
		end;
	end if;
end if;
 
ds_retorno_w	:= ie_duplica_w;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
