CREATE OR REPLACE FUNCTION obter_se_duplic_prescr_solucao ( ie_tipo_solucao_p text, nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


ie_duplic_w	varchar(1)	:= 'N';


BEGIN
if (ie_tipo_solucao_p IS NOT NULL AND ie_tipo_solucao_p::text <> '') and (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then
	begin

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_duplic_w
	from	prescr_solucao
	where	ie_hemodialise	= 'S'
	and	ie_tipo_solucao	= ie_tipo_solucao_p
	and	nr_prescricao	= nr_prescricao_p;
	end;
end if;
return	ie_duplic_w;
end;
$body$
LANGUAGE PLPGSQL
