CREATE OR REPLACE FUNCTION obter_se_edita_qt_proced (cd_intervalo_p text, ie_lado_p text, cd_procedimento_p bigint, ie_origem_proced_p bigint, nr_seq_proc_interno_p bigint, nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_permite_edicao_w		varchar(1);
cd_grupo_proc_w			bigint;
cd_especialidade_w		bigint;
cd_area_procedimento_w		bigint;
cd_convenio_w			bigint;


BEGIN

select	max(cd_grupo_proc),
	max(cd_especialidade),
	max(cd_area_procedimento)
into STRICT	cd_grupo_proc_w,
	cd_especialidade_w,
	cd_area_procedimento_w
from	estrutura_procedimento_v
where	cd_procedimento		= cd_procedimento_p
and	ie_origem_proced	= ie_origem_proced_p;

select	max(obter_convenio_atendimento(nr_atendimento_p))
into STRICT	cd_convenio_w
;

select	max(ie_permite_edicao)
into STRICT	ie_permite_edicao_w
from	prescr_proc_quant
where	coalesce(cd_intervalo,coalesce(cd_intervalo_p,'XPTO'))	= coalesce(cd_intervalo_p,'XPTO')
and	coalesce(ie_lado, coalesce(ie_lado_p,'X')) = coalesce(ie_lado_p,'X')
and	coalesce(cd_procedimento, cd_procedimento_p) = cd_procedimento_p
and	coalesce(cd_area_procedimento, cd_area_procedimento_w)	= cd_area_procedimento_w
and	coalesce(cd_especialidade, cd_especialidade_w)		= cd_especialidade_w
and	coalesce(cd_grupo_proc, cd_grupo_proc_w)			= cd_grupo_proc_w
and	coalesce(cd_convenio, coalesce(cd_convenio_w,0))			= coalesce(cd_convenio_w,0)
and	coalesce(nr_seq_proc_interno, coalesce(nr_seq_proc_interno_p,0))	= coalesce(nr_seq_proc_interno_p,0)
and	((coalesce(cd_procedimento::text, '') = '') or (coalesce(ie_origem_proced, ie_origem_proced_p) = ie_origem_proced_p));

return	coalesce(ie_permite_edicao_w,'S');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
