CREATE OR REPLACE FUNCTION obter_se_efetuar_reserva (nr_seq_transf_resp_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w 	varchar(1)	:= 'N';


BEGIN

if (nr_seq_transf_resp_p IS NOT NULL AND nr_seq_transf_resp_p::text <> '') then
	begin

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_retorno_w
	from 	mprev_transf_resp_programa
	where 	nr_seq_transf_resp		= nr_seq_transf_resp_p;

	if (ie_retorno_w = 'N') then
		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ie_retorno_w
		from 	mprev_transf_part_cic_item
		where 	nr_seq_transf_resp	= nr_seq_transf_resp_p;
	end if;

	end;
end if;

return	ie_retorno_w;

end;
$body$
