SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_eif_escala_lib ( nr_seq_escala_p bigint, ie_html text default 'N') RETURNS varchar AS $body$
DECLARE


cd_pessoa_fisica_w		varchar(10);
ie_retorno_w			varchar(50);
cd_perfil_w				bigint;
cd_estabelecimento_w	bigint;


BEGIN
	cd_pessoa_fisica_w		:= obter_pf_usuario(wheb_usuario_pck.get_nm_usuario,'C');
	cd_perfil_w				:= obter_perfil_ativo;
	cd_estabelecimento_w	:= wheb_usuario_pck.get_cd_estabelecimento;

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_retorno_w
	from	eif_escala_lib
	where	nr_seq_escala = nr_seq_escala_p
	and		coalesce(cd_pessoa_fisica, cd_pessoa_fisica_w) = cd_pessoa_fisica_w
	and		coalesce(cd_perfil, cd_perfil_w) = cd_perfil_w
	and		((coalesce(cd_especialidade::text, '') = '') or (obter_se_especialidade_medico(cd_pessoa_fisica_w,cd_especialidade) = 'S'))
	and		coalesce(cd_estabelecimento, cd_estabelecimento_w) = cd_estabelecimento_w
	and (ie_html = 'N' or ie_permite_insercao = 'S')
	;

	return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
